import React from "react";
import image1 from "../assets/image1.png";
import image2 from "../assets/image2.jpg";
import image3 from "../assets/image3.jpg";
import image4 from "../assets/image4.jpg";

function Card() {
  const animes = [
    {
      anime: "Naruto Shippuden",
      personaje: "Naruto Uzumaki",
      img: image1,
      text: "Naruto Uzumaki es el personaje protagonista del manga y anime Naruto, creado por el mangaka Masashi Kishimoto. En un principio, Kishimoto quería crear un personaje que resultara «simple y estúpido», aunque luego lo dotó con un «pasado oscuro» para, según sus propias palabras, volverlo único.",
    },
    {
      anime: "Naruto Shippuden",
      personaje: "Sasuke Uchiha",
      img: image2,
      text: "Sasuke Uchiha es un personaje ficticio de la serie de manga y anime Naruto, creada por el mangaka Masashi Kishimoto. Inicialmente, el autor no tenía intenciones de crear a Sasuke, pero tras consultarlo con su editor, decidió concebir un rival para el protagonista principal de la obra, Naruto Uzumaki, lo que conllevó a la creación de este.",
    },
    {
      anime: "Dragon Ball Z",
      personaje: "Goku SSJ",
      img: image3,
      text: "Son Gokū es un personaje ficticio, protagonista de la serie de manga y anime Dragon Ball. Fue creado por Akira Toriyama en 1984. Al comienzo de la historia, Gokū aparece como un niño que practica las artes marciales y que posee una cola de mono y una fuerza sobrehumana, pero más adelante se revela que es un extraterrestre de la raza ficticia saiyajin, y que su nombre original es Kakarotto",
    },
    {
      anime: "Dragon Ball Z",
      personaje: "Vegetta SSJ Blue Evolution",
      img: image4,
      text: "Vegeta, también conocido como Principe Vegeta o alternativamente con el estilo Vegeta IV es un personaje de ficción de la franquicia Dragon Ball creado por Akira Toriyama. Vegeta aparece por primera vez en el capítulo #204 Sayonara, Son Goku, publicado en la revista Weekly Shōnen Jump el 7 de noviembre de 1988, buscando las esferas del Dragón que conceden deseos para ganar la inmortalidad.",
    },
  ];

  return animes.map((personaje) => (
    <div className="row">
      <div className="col-md-6">
        <div className="card text-center">
          <div className="card-header text-info">
            <h2>{personaje.anime}</h2>
          </div>
          <img className="card-img-top" src={personaje.img} alt="" />
          <div className="card-body">
            <h4 className="card-title">{personaje.personaje}</h4>
            <p className="card-text">{personaje.text}</p>
          </div>
        </div>
      </div>
    </div>
  ));
}

export default Card;
